package com.ifidc.router.data;

public class MakeTrafficData1 {
	private String id;
	private String title;
	private String intro;
	private String pic;
	private String updatetime;
	private String ContAdd;
	private int img;
	

	public MakeTrafficData1(String id, String title, String pic, String intro,
			String updatetime, String contAdd ,int img) {
		super();
		this.id = id;
		this.title = title;
		this.pic = pic;
		this.intro = intro;
		this.updatetime = updatetime;
		this.img = img;
		this.ContAdd = contAdd;
	}

	public MakeTrafficData1() {
		// TODO Auto-generated constructor stub
	}

	public String getId() {
		return id;
	}

	public void setId(String id) {
		this.id = id;
	}

	public String getTitle() {
		return title;
	}

	public void setTitle(String title) {
		this.title = title;
	}

	public String getPic() {
		return pic;
	}

	public void setPic(String pic) {
		this.pic = pic;
	}

	public String getIntro() {
		return intro;
	}

	public void setIntro(String intro) {
		this.intro = intro;
	}

	public String getUpdatetime() {
		return updatetime;
	}

	public void setUpdatetime(String updatetime) {
		this.updatetime = updatetime;
	}

	public String getContAdd() {
		return ContAdd;
	}

	public void setContAdd(String contAdd) {
		ContAdd = contAdd;
	}

	public int getImg() {
		return img;
	}

	public void setImg(int img) {
		this.img = img;
	}

	@Override
	public int hashCode() {
		final int prime = 31;
		int result = 1;
		result = prime * result + ((ContAdd == null) ? 0 : ContAdd.hashCode());
		result = prime * result + ((id == null) ? 0 : id.hashCode());
		result = prime * result + img;
		result = prime * result + ((intro == null) ? 0 : intro.hashCode());
		result = prime * result + ((pic == null) ? 0 : pic.hashCode());
		result = prime * result + ((title == null) ? 0 : title.hashCode());
		result = prime * result
				+ ((updatetime == null) ? 0 : updatetime.hashCode());
		return result;
	}

	@Override
	public boolean equals(Object obj) {
		if (this == obj)
			return true;
		if (obj == null)
			return false;
		if (getClass() != obj.getClass())
			return false;
		MakeTrafficData1 other = (MakeTrafficData1) obj;
		if (ContAdd == null) {
			if (other.ContAdd != null)
				return false;
		} else if (!ContAdd.equals(other.ContAdd))
			return false;
		if (id == null) {
			if (other.id != null)
				return false;
		} else if (!id.equals(other.id))
			return false;
		if (img != other.img)
			return false;
		if (intro == null) {
			if (other.intro != null)
				return false;
		} else if (!intro.equals(other.intro))
			return false;
		if (pic == null) {
			if (other.pic != null)
				return false;
		} else if (!pic.equals(other.pic))
			return false;
		if (title == null) {
			if (other.title != null)
				return false;
		} else if (!title.equals(other.title))
			return false;
		if (updatetime == null) {
			if (other.updatetime != null)
				return false;
		} else if (!updatetime.equals(other.updatetime))
			return false;
		return true;
	}

}
